select * from sys.databases;
select * from sys.external_data_sources;

DROP DATABASE [appdb];

CREATE DATABASE [appdb];

CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'P@ssword@123';

CREATE DATABASE SCOPED CREDENTIAL sasToken
WITH
    IDENTITY = 'SHARED ACCESS SIGNATURE',
    SECRET = 'sp=r&st=2025-08-06T12:32:19Z&se=2025-09-05T20:47:19Z&spr=https&sv=2024-11-04&sr=c&sig=ZQuTKZvu5eS1C1sgz74k1jCt73crQ1b5oL6oD3U5XW4%3D'
;

CREATE EXTERNAL DATA SOURCE srcActivityLog
WITH 
(
    LOCATION = 'https://datalakestorede.blob.core.windows.net/data',
    CREDENTIAL = sasToken
)
;

CREATE EXTERNAL FILE FORMAT delimitedTxtFileFormat
WITH
(
    FORMAT_TYPE = DELIMITEDTEXT,
    FORMAT_OPTIONS(
        FIELD_TERMINATOR = ',',
        FIRST_ROW = 2

    )
)
;

CREATE EXTERNAL TABLE ActivityLog
(
   [Correlationid] varchar(200),
   [Operationname] varchar(300),
   [Status] varchar(100),
   [Eventcategory] varchar(100),
   [Level] varchar(100),
   [Time] varchar(100),
   [Subscription] varchar(200),
   [Eventinitiatedby] varchar(1000),
   [Resourcetype] varchar(300),
   [Resourcegroup] varchar(1000),
   [Resource] varchar(2000)
)
WITH (
    LOCATION = '/ActivityLog01.csv',
    DATA_SOURCE = srcActivityLog,
    FILE_FORMAT = delimitedTxtFileFormat
)
;

SELECT TOP 10 * FROM ActivityLog;
